import { useState } from "react";
import Counter from "./components/Counter";

function App() {
  const [count, setCount] = useState(0);

const handlePlusOne = () =>{
  setCount(count +1)
}
const handleMInusOne = () =>{
  setCount(count -1)
}
const handleTimesFive = () =>{
  setCount(count *5)
}
  return (
    <div>
      <p>Haz clickado {count} veces</p>
      <button onClick={handlePlusOne}>Pulsame para +1</button>
      <button onClick={handleTimesFive}>Pulsame para *5</button>
      <button onClick={handleMInusOne}>Pulsame para -1</button>
      <button onClick={() => setCount(count - count)}>
        Pulsame para reiniciar
      </button>
    </div>
  );
}

export default App;
